import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import {
    FormControl,
    InputLabel,
    OutlinedInput,
    Checkbox,
    Select,
    TextField
} from '@material-ui/core';

const styles = {
    paper: {
        width: "12rem", padding: "1rem", display: "inline-block", verticalAlign: 'text-top'
    },
    input: {
        marginBottom: "1rem"
    },
    button: {
        width: "50%"
    }
}

const useStyles = makeStyles({
    root: {
      minWidth: 275,
    },
    bullet: {
      display: 'inline-block',
      margin: '0 2px',
      transform: 'scale(0.8)',
    },
    title: {
      fontSize: 14,
    },
    pos: {
      marginBottom: 12,
    },
  });

const Chat = () => {
    const classes = useStyles();
    const bull = <span className={classes.bullet}>•</span>;
    const [socket] = useState(() => io(':8000'))
    const [messages, setMessages] = useState([]);
    const [message, setMessage] = useState("");
    const [username, setUsername] = useState("");
    const [typingStatus, setTypingStatus] = useState('');

    useEffect(() => {
        return () => socket.disconnect(true);
    }, []);

    socket.on('messageSent', data => {
        setMessages([...messages, data]);
        setTypingStatus('');
    })

    socket.on('clientTyping', data => {
        setTypingStatus(data.message);
    })

    const submitHandler = e => {
        e.preventDefault();

        socket.emit('sendMessage', {username, message})
        setTypingStatus('');
        setMessage('');
    }

    const typingHandler = e => {
        setMessage(e.target.value);
        socket.emit('clientTyping', {message: `${username} is typing...`})
    }
    return (
        <>
        <Card className={classes.root} variant="outlined">
            <CardContent>
                <Typography className={classes.title} color="textSecondary" gutterBottom>
                Word of the Day
                </Typography>
                <Typography variant="h5" component="h2">
                be{bull}nev{bull}o{bull}lent
                </Typography>
                <Typography className={classes.pos} color="textSecondary">
                adjective
                </Typography>
                <Typography variant="body2" component="p">
                well meaning and kindly.
                <br />
                {'"a benevolent smile"'}
                </Typography>
            </CardContent>
        <CardActions>
        <div elevation={1} style={styles.paper}>
        <FormControl variant="outlined" onSubmit={ submitHandler} style={styles.input}>
                <p>{typingStatus}</p>
                <OutlinedInput type="text" name="message" onChange={ typingHandler } value={ message }/>
                <TextField id="outlined-basic" label="Message" variant="outlined" type="text" name="message" onChange={typingHandler} value={message}/>
                <Button type="submit" variant="contained" color="primary">
                    Send
                </Button>
        </FormControl>
        </div>
        </CardActions>
    </Card>
            <input type="text" name="username" onChange={e => setUsername(e.target.value) } placeholder="username"/>
            <form onSubmit={ submitHandler }>
                <p>{typingStatus}</p>
                <input type="text" name="message" onChange={ typingHandler } value={ message }/>
                <input type="submit" value="Send"/>
            </form>
            <div>
                {
                    messages.map( (item, i) => 
                        <p key={i}>{item.username}: {item.message}</p>
                    )
                }
            </div>
        </>
    );
}

export default Chat
