import React, { useEffect, useState } from 'react';
import Axios from 'axios';
import { navigate } from '@reach/router';
import { Table, TableBody, TableCell, TableHead, TableRow, Button, AppBar, Toolbar, IconButton, Typography, Box } from '@material-ui/core';
import img from '../Neighbourly.PNG';
import { makeStyles } from '@material-ui/core/styles';

export default props => {
    const [allusers, setAllUsers] = useState([]);
    const [tools, setTools] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        Axios.get(`http://localhost:8000/api/user`)
            .then(res => {
                console.log("**********", res)
                setAllUsers(res.data.results)
                setLoading(false)
            })
            .catch(err => console.log(err))
    }, []);

    if(loading){
        return(
            <p>Loading....</p>
        )
    }

    return (
        <div >
<AppBar position="static" style={{height: '10px'}}>
  <Toolbar>
    <IconButton edge="start" color="inherit" aria-label="menu">
      {/* <MenuIcon /> */}
    </IconButton>
    <Typography variant="h6">
        <div>
            <Box letterSpacing={3}>
            <img src={img} style={{width: '30px', height: '30px', display: 'inline-block', verticalAlign: 'middle', marginRight: '5px'}}/>
            <h3 style={{display: 'inline-block', verticalAlign: 'middle'}}>Neighborly</h3> 
            </Box>
        </div>
    </Typography>
    <Button color="inherit">Logout</Button>
  </Toolbar>
</AppBar>

            <a href="/">Logout</a>
            < h1 > These users are looking for share tools</h1>
            {/* <Link to="/user/new" > Add a user to the list </Link> */}
            <br/>
            <br/>
            
            <center><Table className="table table-danger col-8 mx-auto">
                <TableHead>
                    <TableRow>
                        <TableCell> Full Name </TableCell>
                        <TableCell> Type </TableCell>
                        <TableCell> Actions </TableCell>
                    </TableRow>
                </TableHead>
                <TableBody>
                {
                allusers.map((user, index) => {
                    return (
                        <TableRow key={index}>
                            <TableCell> {user.firstName} {user.lastName} </TableCell>
                            <TableCell> {user?.tools?.type} </TableCell>
                            <TableCell>
                                <Button style={{marginRight: '5px'}} variant="outlined" color="primary" onClick={e => navigate(`/user/${user._id}`)}> Details </Button>
                                <Button style={{marginRight: '5px'}} variant="outlined" color="primary" onClick={e => navigate(`/user/${user._id}/edit`)}> Edit </Button>
                                <Button style={{marginRight: '5px'}} variant="outlined" color="primary" onClick={e => navigate(`/user/${user._id}/connect`)}> Connect </Button>
                                <Button variant="outlined" color="primary" onClick={e => navigate(`/user/${user._id}/new_tool`)}> Add Tool </Button>
                            </TableCell>
                        </TableRow>)
                })
                }
                </TableBody>
            </Table></center>
        </div>
    )
}
