                    value={lastName} /><br/>
                <span> {errors.lastName ? errors.lastName.message : ''} </span>
                <br/>

                <label htmlFor="address" > Address: </label>
                <input type="text" name="address" onChange={(e) => setAddress(e.target.value)}
                    value={address} />
                <br/>

                {/* <label htmlFor="Type">  Type: </label>
